import { Link as RouterLink, useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import * as Yup from 'yup';
import { Formik } from 'formik';
import {
    Box,
    Button,
    Container,
    Grid,
    Link,
    TextField,
    Typography
} from '@material-ui/core';

import React from 'react';
import { makeStyles } from '@material-ui/styles';
import Card from '@material-ui/core/Card';
import Forget from 'src/components/Forget';
import zIndex from '@material-ui/core/styles/zIndex';

// 

import {  InputAdornment, IconButton } from "@material-ui/core";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";

const useStyles = makeStyles({
    root: {
        minWidth: 275,
        marginTop: "150px",
        backgroundColor: "white",
        zIndex: 2
    },
});


const ChangePassword = () => {
    const classes = useStyles();
    const navigate = useNavigate();

    function moveTO() { }

    //   

    const [showPassword, setShowPassword] = useState(false);
    const handleClickShowPassword = () => setShowPassword(!showPassword);
    const handleMouseDownPassword = () => setShowPassword(!showPassword);

    return (
        <>
            <Helmet>
                <title>Login | Material Kit</title>
            </Helmet>

            <Container style={{ display: "flex", justifyContent: "center", textAlign: "center" }}>

                <Card className={classes.root}>

                    <Box
                        sx={{
                            // backgroundColor: 'white',
                            display: 'flex',
                            flexDirection: 'column',
                            height: '100%',
                            justifyContent: 'center',

                        }}
                    >
                        <Container maxWidth="sm">
                            <Formik
                                initialValues={{
                                    email: 'demo@devias.io',
                                    password: 'Password123'
                                }}
                                validationSchema={Yup.object().shape({
                                    email: Yup.string().email('Must be a valid email').max(255).required('Email is required'),
                                    password: Yup.string().max(255).required('Password is required')
                                })}
                                onSubmit={() => {
                                    navigate('/app/dashboard', { replace: true });
                                }}
                            >
                                {({
                                    errors,
                                    handleBlur,
                                    handleChange,
                                    handleSubmit,
                                    isSubmitting,
                                    touched,
                                    values
                                }) => (
                                    <form onSubmit={handleSubmit}>
                                        <Box style={{ display: 'flex', justifyContent: "center", marginBottom: "20px", marginTop: "20px" }}>
                                            <img width="250px" src="static/images/avatars/logo.png" />
                                        </Box>
                                        <Box sx={{ mb: 3 }}>
                                            <Typography
                                                color="textPrimary"
                                                variant="h4"
                                                paddingLeft="10px"
                                            >
                                                Change Password
                                            </Typography>

                                        </Box>
                                        <Grid
                                            container
                                            spacing={3}
                                        >


                                        </Grid>


                                        <TextField
                                            error={Boolean(touched.password && errors.password)}
                                            fullWidth
                                            helperText={touched.password && errors.password}
                                            label="Current Password"
                                            margin="normal"
                                            name="password"
                                            onBlur={handleBlur}
                                            onChange={handleChange}
                                            type="password"
                                            value={values.password}
                                            variant="outlined"
                                        />
                                        <TextField
                                            error={Boolean(touched.password && errors.password)}
                                            fullWidth
                                            helperText={touched.password && errors.password}
                                            label="New Password"
                                            margin="normal"
                                            name="password"
                                            onBlur={handleBlur}
                                            onChange={handleChange}
                                            type="password"
                                            value={values.password}
                                            variant="outlined"
                                        />
                                        <TextField
                                            error={Boolean(touched.password && errors.password)}
                                            fullWidth
                                            helperText={touched.password && errors.password}
                                            label="Confirm Password"
                                            margin="normal"
                                            name="password"
                                            onBlur={handleBlur}
                                            onChange={handleChange}
                                            type="password"
                                            value={values.password}
                                            variant="outlined"
                                        />

                                        <TextField
                                            label='Some label'
                                            variant="outlined"
                                            type={showPassword ? "text" : "password"} // <-- This is where the magic happens
                                            onChange={handleChange}
                                            InputProps={{ // <-- This is where the toggle button is added.
                                                endAdornment: (
                                                    <InputAdornment position="end">
                                                        <IconButton
                                                            aria-label="toggle password visibility"
                                                            onClick={handleClickShowPassword}
                                                            onMouseDown={handleMouseDownPassword}
                                                        >
                                                            {showPassword ? <Visibility /> : <VisibilityOff />}
                                                        </IconButton>
                                                    </InputAdornment>
                                                )
                                            }}
                                        />



                                        <Box sx={{ py: 2 }}>
                                            <Button
                                                color="primary"
                                                disabled={isSubmitting}
                                                fullWidth
                                                size="large"
                                                type="submit"
                                                variant="contained"
                                                onClick={moveTO}
                                            >
                                                Sign in now
                                            </Button>
                                        </Box>
                                        <Box style={{ display: "flex", justifyContent: "end", marginBottom: "15px" }}>

                                            <Link component={RouterLink} to="/forget" variant="h6" underline="hover">
                                                Forget Password?
                                            </Link>
                                        </Box>

                                    </form>
                                )}
                            </Formik>
                        </Container>
                    </Box>
                </Card>
            </Container>
        </>
    );
};

export default ChangePassword;
