import customers from '../__mocks__/customers';

const INITIAL_STATE = {
    profile: {
        email: 'abc@gmail.com',
        userName: "Katarina Smith",
        password: 123,
        post: "Senior Developer"
    },

    profiles: customers,
    User: {id: ""}
}

const reducer = (state = INITIAL_STATE, action ) => {
    switch (action.type) {
        case "AUTH" :
            return {
                ...state,
                User: state.profiles.find((profile) =>  profile.id == action.payload.email && profile.password == action.payload.password)
            }
    }
}

export default reducer;
