import { Link as RouterLink, useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import * as Yup from 'yup';
import { Formik } from 'formik';
import {
  Box,
  Button,
  Container,
  Grid,
  Link,
  TextField,
  Typography
} from '@material-ui/core';

import React from 'react';
import { makeStyles } from '@material-ui/styles';
import Card from '@material-ui/core/Card';
import Forget from 'src/components/Forget';
import zIndex from '@material-ui/core/styles/zIndex';

const useStyles = makeStyles({
  root: {
    minWidth: 275,
    marginTop: "150px",
    backgroundColor: "red",
    zIndex: 2
  },
});


const Login = () => {
  const classes = useStyles();
  const navigate = useNavigate();

  function moveTO() { }

  return (
    <>
      <Helmet>
        <title>Login | Material Kit</title>
      </Helmet>

      <Container style={{display: "flex", justifyContent: "center", textAlign: "center"}}>

        <Card style={{display: "flex", justifyContent: "center", textAlign: "center"}} className={classes.root}>

          <Box
            sx={{
              // backgroundColor: 'white',
              display: 'flex',
              flexDirection: 'column',
              height: '100%',
              justifyContent: 'center',

            }}
          >
            {/* <Container maxWidth="sm"> */}
              <Formik
                initialValues={{
                  email: 'demo@devias.io',
                  password: 'Password123'
                }}
                validationSchema={Yup.object().shape({
                  email: Yup.string().email('Must be a valid email').max(255).required('Email is required'),
                  password: Yup.string().max(255).required('Password is required')
                })}
                onSubmit={() => {
                  navigate('/app/dashboard', { replace: true });
                }}
              >
                {({
                  errors,
                  handleBlur,
                  handleChange,
                  handleSubmit,
                  isSubmitting,
                  touched,
                  values
                }) => (
                  <form onSubmit={handleSubmit}>
                    <Box style={{ display: 'flex', justifyContent: "center", marginBottom: "20px", marginTop: "20px" }}>
                      <img width="250px" src="static/images/avatars/logo.png" />
                    </Box>
                    <Box sx={{ mb: 3 }}>
                      <Typography
                        color="textPrimary"
                        variant="h4"
                        paddingLeft="10px"
                      >
                        Sign in
                      </Typography>

                    </Box>
                    <Grid
                      container
                      spacing={3}
                    >


                    </Grid>

                    <TextField
                      error={Boolean(touched.email && errors.email)}
                      fullWidth
                      helperText={touched.email && errors.email}
                      label="Email Address"
                      margin="normal"
                      name="email"
                      onBlur={handleBlur}
                      onChange={handleChange}
                      type="email"
                      value={values.email}
                      variant="outlined"
                    />
                    <TextField
                      error={Boolean(touched.password && errors.password)}
                      fullWidth
                      helperText={touched.password && errors.password}
                      label="Password"
                      margin="normal"
                      name="password"
                      onBlur={handleBlur}
                      onChange={handleChange}
                      type="password"
                      value={values.password}
                      variant="outlined"
                    />
                    <Box sx={{ py: 2 }}>
                      <Button
                        color="primary"
                        disabled={isSubmitting}
                        fullWidth
                        size="large"
                        type="submit"
                        variant="contained"
                        onClick={moveTO}
                      >
                        Sign in now
                      </Button>
                    </Box>
                    <Box style={{ display: "flex", justifyContent: "end" ,marginBottom: "15px"}}>
                    {/* <RouterLink to="forget">
                      <p>Forget Password?</p>
                    </RouterLink> */}
                      <Link  to="/forget" variant="h6" underline="hover">
                        Forget Password ?
                      </Link>
                    </Box>

                  </form>
                )}
              </Formik>
            {/* </Container> */}
          </Box>
        </Card>
      </Container>
    </>
  );
};

export default Login;
